import React from 'react';
import Button from 'react-bootstrap/Button';
import DropdownButton from 'react-bootstrap/DropdownButton';
import Dropdown from 'react-bootstrap/Dropdown';
import './style.scss';

class Selector extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      url: "",
      routeType: ""
    }
  }
  handleChange = (e) => {
    let url = e.target.value;
    this.setState({ url })
  }

  handleClick = (e) => {
    e.preventDefault();
    // this.setState({ url })
    console.log(this.state.url, this.state.routeType)
  }

  handleSelect = (e) => {
    let routeType = e.target.value;
    console.log(routeType);
    this.setState({ routeType })
  }

  render() {
    return (

      <div className="input-section">
        <h3 className="select-header">Enter your URL and Route</h3>

        <DropdownButton id="dropdown-basic-button" title="Dropdown button" onChange={this.handleSelect}>
          <Dropdown.Item value="GET">GET</Dropdown.Item>
          <Dropdown.Item value="POST">POST</Dropdown.Item>
          <Dropdown.Item value="PUT">PUT</Dropdown.Item>
          <Dropdown.Item value="DELETE">DELETE</Dropdown.Item>
        </DropdownButton>
        {/* <section >
          <input type="checkbox" id="get" name="get" value="GET"></input>
          <label className="select-option" for="get">GET</label><br/>
          <input type="checkbox" id="post" name="post" value="POST"></input>
          <label className="select-option" for="post">POST</label><br/>
          <input type="checkbox" id="put" name="put" value="PUT"></input>
          <label className="select-option" for="put">PUT</label><br/>
          <input type="checkbox" id="delete" name="delete" value="DELETE"></input>
          <label className="select-option" for="put">DELETE</label><br/>
        </section> */}
        <input type='text' placeholder='Add URL Here' onChange={this.handleChange}></input>
        <Button type="submit" size="lg" className="bg-danger text-light" variant="secondary" onClick={this.handleClick}>Submit</Button>
        <p className="select-option" >{this.state.routeType} {this.state.url}</p>
      </div>
    )
  }
}

export default Selector;
