import React from 'react';
import Header from './header.js';
import Footer from './footer.js';
import Form from './form.js';
import Result from './results.js';
import Loader from './loader.js';
import History from './history.js';
import superagent from 'superagent';
import { If, Then, When, Unless } from 'react-if';
import './style.scss';

class App extends React.Component {
  constructor(props) {
    super(props);
    this.state = {
      loading: false,
      count: 0,
      sessionCounter: 0,
      results: [],
      history: [],
      url: '',
      route: '',
      body: ''
    }
  }

  handleForm = (count, results, url, route) => {
    this.setState({ count, results, url, route});
  }
  handleHistory = (url, route) => {
    this.setState({ url, route});
  }

  toggleLoading = () => {
    this.setState({ loading: !this.state.loading });
  }
  handleBody = (body) => {
    this.setState({ body });
    console.log(`inside handleForm ${this.state.body}`);
  }
  handleClick = async (e) => {
    e.preventDefault();
    this.toggleLoading();
    // let route = this.state.routeType;
    // let url = this.state.url;
    sessionStorage.setItem(this.state.counter, (`${route}, ${url}`));
    let storageCount = this.state.sessionCounter + 1;
    this.setState({ counter: storageCount });
    console.log(url, route);
    try{

      let raw = await superagent(route, url);
      let results = raw.body.results;
      // let results = await response.json();
      console.log(results);
      // let entries = data.results;
      // let results = Object.entries(entries);
      let count = results.count;
      setTimeout(() => {
        this.props.handler(count, results, url, route, body);
        this.props.toggleLoading();
      }, 1000);
      } catch (e) {
        setTimeout(() => {
        this.props.toggleLoading();
        this.props.handler(e);
      }, 1000);
    }
  }

  render() {
    return(
      <div>
        <Header />
        <If condition={this.state.loading}>
          <Then>
            <Loader/>
          </Then>
        </If>
        <Form prompt="SEND" 
        toggleLoading={this.toggleLoading} 
        handler={this.handleForm}
        handleBody={this.handleBody}/>
         {this.state.count !== 0 ?
         <>
         <Result results={this.state.results}/>
         <History history={this.state.history} handler={this.handleHistory}></History>
          </>
          : '' }
        <Footer />

      </div>
    )
  }
}

export default App;
